import { Component, OnInit } from '@angular/core';
import { OrderRm, FoodRm, OrderItem, UserRm, OrderDto } from '../api/models';
import { CategoryService, FoodService, UserService } from '../api/services';
import { ActivatedRoute, Router } from '@angular/router';
import { OrderService } from './../api/services/order.service';
import { AuthService } from './../auth/auth.service';
import { forkJoin } from 'rxjs';
import { v4 as uuidv4 } from 'uuid';

@Component({
  selector: 'app-my-orders',
  templateUrl: './my-orders.component.html',
  styleUrls: ['./my-orders.component.css']
})
export class MyOrdersComponent {

  orders: OrderRm[] = [];
  foods: FoodRm[] = [];

  constructor(private route: ActivatedRoute,
    private router: Router,
    private authService: AuthService,
    private foodService: FoodService,
    private orderService: OrderService,
    private userService: UserService) { }


  ngOnInit(): void {
    this.searchOrders();
  }

  searchOrders() {
    this.orderService.listOrder({ email: this.authService.currentUser?.email! })
      .subscribe(l => this.orders = l,
        err => this.handleError(err));
  }



  private handleError(err: any) {
    console.log("Response Error, Status:", err.status);
    console.log("Response Error, Status Text:", err.statusText);
    console.log(err);
  }


}
