import { Component, OnInit } from '@angular/core';
import { CategoryService, FoodService, OrderService } from '../api/services';
import { ActivatedRoute, Router } from '@angular/router';
import { FoodRm, OrderDto, FoodDto } from '../api/models';
import { FormBuilder, Validators, FormGroup } from '@angular/forms';
import { AuthService } from '../auth/auth.service';
import { v4 as uuidv4 } from 'uuid';


@Component({
  selector: 'app-create-food',
  templateUrl: './create-food.component.html',
  styleUrls: ['./create-food.component.css']
})
export class CreateFoodComponent {

  foodId: string = 'not loaded';
  food: FoodRm = {};

  form!: FormGroup;

  constructor(private route: ActivatedRoute,
    private router: Router,
    private authService: AuthService,
    private foodService: FoodService,
    private orderService: OrderService,
    private formBuilder: FormBuilder,
    private categoryService: CategoryService) { }


  ngOnInit(): void {

    this.form = this.formBuilder.group({
      imageUrl: ['', Validators.required],
      categoryId: ['', Validators.required],
      name: ['', Validators.required],
      description: ['', Validators.required],
      price: ['', Validators.required],
      preperationTime: ['', Validators.required]
    });

  }

  createFood() {
    console.log(this.form);

    let dto: FoodDto = {
      categoryId: this.form.value.categoryId,
      description: this.form.value.description,
      id: uuidv4(),
      imageUrl: this.form.value.imageUrl,
      name: this.form.value.name,
      preperationTime: Number(this.form.value.preperationTime),
      price: Number(this.form.value.price)
    }

    console.log(dto);

    this.foodService.createFood({ body: dto })
      .subscribe(_ => {
        this.router.navigate(['/menu']);
      },
        err => this.handleError(err));


  }


  cancel() {

  }


  private handleError = (err: any) => {

    if (err.status == 404) {
      alert("Food not found!")
      this.router.navigate(['/menu'])
    }


    if (err.status == 409) {
      console.log("err: " + err);
      alert(JSON.parse(err.error).message)
    }

    console.log("Response Error. Status: ", err.status)
    console.log("Response Error. Status Text: ", err.statusText)
    console.log(err)
  }

  getCategories() {
    this.categoryService.getAllCategoriesCategory()
      .subscribe(rmList => this.categories = rmList,
        err => this.handleError(err));
  }

  private handleError(err: any) {

    console.log(err);
  }
}

<div class="container">
    <form [formGroup]="form">
        <div class="card mb-3" style="max-width: 100%;">
            <div class="row">
                <!--food image-->
                <div class="col-md-4">
                    <div class="form-group">
                        <label for="imagePath">Image URL</label>
                        <input type="text"
                               id="imagePath"
                               formControlName="imageUrl"
                               class="form-control"
                               #imageUrl>
                    </div>
                    <div class="row">
                        <img [src]="imageUrl.value" class="img-responsive">
                    </div>
                </div>


                <!--food information-->
                <div class="col-md-8">

                    <div class="card-body">

                        <!--category-->
                        <div class="form-group">
                            <label for="categoryId">Category</label>
                            <select class="form-select form-select-sm" formControlName="categoryId" id="categoryId" required>
                                <option disabled selected>Open this select menu</option>
                                <option value="1">Pizza</option>
                                <option value="2">Burger</option>
                                <option value="3">Breakfast</option>
                                <option value="4">Sushi</option>
                                <option value="5">Soup</option>
                                <option value="6">Salad</option>
                            </select>
                        </div>
                        <!--name-->
                        <div class="form-group">
                            <label for="name">Name</label>
                            <input type="text"
                                   class="form-control"
                                   id="name"
                                   formControlName="name"
                                   required>
                        </div>
                        <!--description-->
                        <div class="form-group">
                            <label for="description">Description</label>
                            <textarea type="text"
                                      class="form-control"
                                      id="description"
                                      formControlName="description"
                                      required>

                        </textarea>
                        </div>
                        <!--price-->
                        <div class="form-group">
                            <label for="price">Price</label>
                            <input formControlName="price"
                                   type="number" class="form-control"
                                   id="price" name="price"
                                   style="max-width: 30%;"
                                   required />
                        </div>
                        <!--preperationTime-->
                        <div class="form-group">
                            <label for="preperationTime">Preperation Time</label>
                            <input formControlName="preperationTime"
                                   type="number" class="form-control"
                                   id="preperationTime" name="preperationTime"
                                   style="max-width: 30%;" />
                        </div>
                        <!--submit button-->
                        <br />
                        <button (click)="createFood()" type="submit" class="btn">Submit</button>
                        <!--remove button-->
                        <button (click)="cancel()" type="button" class="btn delete-button">Cancel</button>
                    </div>


                </div>

            </div>
        </div>
    </form>
</div>
