import { Component } from '@angular/core';

@Component({
  selector: 'app-food-list',
  templateUrl: './food-list.component.html',
  styleUrls: ['./food-list.component.css']
})
export class FoodListComponent {


  foodMap = new Map<string, Food[]>([
    ["Pizza", [
      new FoodRm("Denizci", "Özel Pizza Sosumuz, mozarella, mısır, taze soğan, domates ve ton balığı ile denize açılma vakti!",
        "https://www.papajohns.com.tr/upload/big/image5e56fb9cd11bc.jpg",
        35, 15),
      new FoodRm("Vegan", "Hayvansal gıda içermeyen (Mozeralla içermeyen) veganlara özel yaptığımız, Taze Domates,Taze Mantar, Soğan,Yeşil Biber,Siyah Zeytin ve Mısır'dan oluşan Vegan Pizzamız.",
        "https://www.papajohns.com.tr/upload/big/image5e56fbf83ea3f.jpg", 32, 12)
    ]],
    ["Burger", [
      new FoodRm("Hesaplı Menü", "1 McChicken™ + 1 Orta Patates + 1 Orta Coca-Cola + 8'li Çıtır Soğan Halkaları",
        "https://siparis.mcdonalds.com.tr/Files/Product/Menus/WEB3_1076.jpg", 25, 10),
      new FoodRm("Köfte Burgerli Tok Artık Menüsü", "4 Adet Köfte Burger + 1 Adet Büyük Boy Patates + 2 Adet Orta Boy İçecek",
        "https://siparis.mcdonalds.com.tr/Files/Product/Menus/WEB3_1005.jpg", 22, 12)
    ]],
    ["Breakfast", [
      new FoodRm("Sıcak Kahvaltı Tabağı", "Haşlanmış yumurta, Reçel, ızgara hellim peyniri, Kızarmış sosis, Domates, Salatalık, Zeytin, Sigara böreği.",
        "https://cdn.yemek.com/mnresize/1250/833/uploads/2021/03/lokma-rumeli-hisari-kahvalti-2022.jpg", 40, 8)
    ]],
    ["Sushi", [
      new FoodRm("Pad Thai Tavuk", "Pirinç eriştesi, tavuk göğüs, soya filizi, taze soğan, fıstık, yumurta, lime ve pul biber ile",
        "https://www.sushico.com.tr/site/products/big/1682930248_1.jpg", 20, 6),
      new FoodRm("Süper Salmon Roll", "Somon, krem peynir, salatalık / Kızarmış panko, yuzu mayonez ",
        "https://www.sushico.com.tr/site/products/big/1682930694_1.jpg", 15, 7),
      new FoodRm("Pad Thai Karides", "Pirinç eriştesi, karides, soya filizi, taze soğan, fıstık, yumurta, lime ve pul biber ile. ",
        "https://www.sushico.com.tr/site/products/big/1682930406_1.jpg", 20, 10),
      new FoodRm("Tom Yum", "Karides, shitake mantar, çeri domates, limon çubuğu, kırmızı soğan, kişniş,",
        "https://www.sushico.com.tr/site/products/big/1682929149_1.jpg", 15, 5)
    ]],
    ["Soup", [
      new FoodRm("Deniz Mahsüllü Çorba", "Hindistan cevizi sütü, levrek, somon, kalamar, kırmızı soğan, havuç, çin acı sosu, kişniş, ",
        "https://www.sushico.com.tr/site/products/big/1667254274_1.jpg", 24, 8),
      new FoodRm("Wonton Çorbası", "Çin Mantısı (3 Ad), taze soğan, yumurta.",
        "https://www.sushico.com.tr/site/products/big/1303651819_1.jpg", 15, 5)
    ]],
    ["Salad", [
      new FoodRm("Füme Somonlu Asian Salata", "Somon füme, soya filizi, havuç, kırmızı soğan, domates, mix yeşillik, fıstık, avokado, mikro filiz, yuzu soya sos ile.",
        "https://www.sushico.com.tr/site/products/big/1682929692_1.jpg", 20, 4),
      new FoodRm("Asian Salad", "Soya filizi, havuç, kırmızı soğan, domates, mix yeşillik, fıstık, avokado, mikro filiz, yuzu soya sos ile.",
        "https://www.sushico.com.tr/site/products/big/1667257901_1.jpg", 18, 12)
    ]]
  ]);
}


export interface FoodRm {
  name: string;
  description: string;
  imageUrl: string;
  preperationTime: number; // minutes
  price: number;           // $
}
