import { Component, OnInit, Renderer2 } from '@angular/core';
import { AuthService } from './../auth/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {

  constructor(private authService: AuthService,
    private router: Router,
    private renderer: Renderer2) { }

  ngOnInit(): void {
  }

  isUser() {
    return this.authService.currentUser != null && this.authService.admin == false;
  }

  isAdmin() {
    return this.authService.admin;
  }


  redirectToMenu() {
    this.router.navigate(['/menu']);
  }

  logout() {
    this.authService.currentUser = undefined;
    this.authService.admin = false;

    // Get the #slogan element by its ID
    const sloganElement = document.getElementById('slogan');

    if (sloganElement) {
      // Add the class to trigger the animation
      this.renderer.addClass(sloganElement, 'animate-fade-in');

      // Remove the class after the animation completes
      setTimeout(() => {
        this.renderer.removeClass(sloganElement, 'animate-fade-in');
      }, 1000); // Adjust the timeout to match your animation duration (1s in this case)
    }

    this.router.navigate(['/menu']);
  }
}
