import { Component, OnInit } from '@angular/core';
import { OrderRm, FoodRm, OrderItem, UserRm, OrderDto } from '../api/models';
import { CategoryService, FoodService, UserService, OrderService } from '../api/services';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from './../auth/auth.service';
import { forkJoin } from 'rxjs';


@Component({
  selector: 'app-order-detail',
  templateUrl: './order-detail.component.html',
  styleUrls: ['./order-detail.component.css']
})
export class OrderDetailComponent implements OnInit{

  orderId: string = 'not loaded';
  order: OrderRm = {};
  orderItems: OrderItem[] = [];
  foods: FoodRm[] = [];

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private authService: AuthService,
    private foodService: FoodService,
    private orderService: OrderService,
    private userService: UserService) { }


  ngOnInit(): void {

    this.route.paramMap
      .subscribe(p => this.findOrder(p.get("orderId")));

    this.orderService.getOrderItemsOrder({ orderId: this.orderId })
      .subscribe(
        (orderItems) => {
          this.orderItems = orderItems;
          console.log(this.orderItems);
          // Fetch food data for each order
          const foodObservables = orderItems.map((orderItem) =>
            this.foodService.findFood({ id: orderItem.foodItemId! })
          );

          forkJoin(foodObservables).subscribe(
            (foods) => {
              // All food requests have completed here
              this.foods = foods;
            },
            (err) => this.handleError(err)
          );
        },
        (err) => this.handleError(err)
      );

  }

  private findOrder = (orderId: string | null) => {
    this.orderId = orderId ?? 'not passed';

    this.orderService.getOrderByOrderIdOrder({ orderId: orderId! })
      .subscribe(
        order => {
          this.order = order;
        },
        err => this.handleError(err)
      );
  }


  private handleError = (err: any) => {

    if (err.status == 404) {
      alert("Food not found!")
      this.router.navigate(['/menu'])
    }


    if (err.status == 409) {
      console.log("err: " + err);
      alert(JSON.parse(err.error).message)
    }

    console.log("Response Error. Status: ", err.status)
    console.log("Response Error. Status Text: ", err.statusText)
    console.log(err)
  }
}
